import React, {Component} from "react";
import ReactDOM from "react-dom";
import "./assets/style.css"
import quizService from "./quizService"
import QuestionBox from "./components/QuestionBox"


class QuizBee extends Component {
    state = {
        questionBank: [],
        score: 0,
        displayAnswers: "none"
    };

    computeAnswer = (answer, questionId ) => {
        this.setState((state) => ({
            questionBank: state.questionBank.map( question => {
                if( question.questionId === questionId ){
                    return {
                        ...question,
                        userAnswer: answer
                    }
                }
                return {...question}
            })
        }))
        this.setState((state) => ({
            questionBank: state.questionBank,
            score: state.questionBank.filter(question => question.correct === question.userAnswer).length
        }))
    }

    getQuestions = () => {
        quizService().then( response => {
            this.setState({
                questionBank: response.map(x => {
                    return {
                        ...x,
                        userAnswer: ""
                    }
                })
            });
        });  
    }

    componentDidMount(){
        this.getQuestions();

    }

    onSubmitHandle = (id) => {
         this.setState({
             displayAnswers:"block"
         })
    }

    render(){
        return (
            <div className='container'>
                <div className='title'> QuizBee </div>
                { this.state.questionBank.length > 0 && 
                this.state.questionBank.map( ({question, answers, correct, questionId, userAnswer}) => 
                    <div>
                        <QuestionBox 
                            question={question} 
                            options={answers} 
                            key={questionId}
                            selected={answer => this.computeAnswer(answer, questionId)}/>
                        <div style={{display:this.state.displayAnswers}}>
                            <p> Correct answer: {correct}</p>
                        </div>
                    </div>
                )};
                <div className='submitBtn' style={{display:this.state.displayAnswers!=="block"}}>
                    <button onClick={this.onSubmitHandle}> Submit Quiz </button>
                </div>
            </div>

        )
    }
}

ReactDOM.render(<QuizBee/>, document.getElementById("root"));
