import React, {Component} from "react";
import ReactDOM from "react-dom";
import "./assets/style.css"
import quizService from "./quizService"
import QuestionBox from "./components/QuestionBox"


class QuizBee extends Component {
    state = {
        questionBank: [],
        scores: [0, 0, 0, 0, 0],
        responses: 0
    };

    computeAnswer = (answer, correctAnswer, questionIndex) => {
        if( answer === correctAnswer ){
            this.setState({
                ... this.state,
                scores : this.scores.map((score, index) => {
                    // return {score}
                    if( index === questionIndex )
                        return {score};
                    return {score};
                })
            })
        }
    }

    getQuestions = () => {
        quizService().then(question => {
            this.setState({
                questionBank: question
            });
        });
    }

    componentDidMount(){
        this.getQuestions();
    }

    render(){
        return (
            <div className='container'>
                <div className='title'> QuizBee </div>
                { this.state.questionBank.length > 0 && 
                this.state.questionBank.map( ({question, answers, correct, questionId}, index) => 
                    <QuestionBox 
                        question={question} 
                        options={answers} 
                        key={questionId}
                        selected={answer => this.computeAnswer(answer, correct, index)}/>
                )};
                <div className='submitBtn'>
                    <button > Submit Quiz </button>
                </div>
            </div>

        )
    }
}

ReactDOM.render(<QuizBee/>, document.getElementById("root"));
