import React, {Component} from "react";
import ReactDOM from "react-dom";
import "./assets/style.css"
import quizService from "./quizService"
import QuestionBox from "./components/QuestionBox"


class QuizBee extends Component {
    state = {
        questionBank: [],
        score: 0
    };

    computeAnswer = (answer, questionId ) => {
        this.setState({
            score: 0,
            questionBank: this.state.questionBank.map( question => {
                if( question.questionId === questionId ){
                    return {
                        ...question,
                        userAnswer: answer
                    }
                }
                return {...question}
            })
        })
        // console.log("Submitted", this.state.questionBank)

    }

    getQuestions = () => {
        quizService().then( response => {
            this.setState({
                questionBank: response.map(x => {
                    return {
                        ...x,
                        userAnswer: ""
                    }
                })
            });
        });

    }

    componentDidMount(){
        this.getQuestions();
    }

    onSubmitHandle(id){
        this.setState({
            questionBank: this.state.questionBank,
            score: this.questionBank.reduce( (a, v) => a = a + (v.userAnswer === v.correct), 0)
        })
        console.log(this.state.score)  
        // console.log("Submitted", this.state.questionBank)
    }

    render(){
        return (
            <div className='container'>
                <div className='title'> QuizBee </div>
                { this.state.questionBank.length > 0 && 
                this.state.questionBank.map( ({question, answers, correct, questionId, userAnswer}) => 
                    <QuestionBox 
                        question={question} 
                        options={answers} 
                        key={questionId}
                        selected={answer => this.computeAnswer(answer, questionId)}/>
                )};
                <div className='submitBtn'>
                    <button onClick={this.onSubmitHandle}> Submit Quiz </button>
                </div>
            </div>

        )
    }
}

ReactDOM.render(<QuizBee/>, document.getElementById("root"));
